INSERT INTO `wp_options` VALUES("259", "_transient_dash_fe2922e4dab38d163882ac0453e47f7b", "<div class=\"rss-widget\"><ul><li><a class=\'rsswidget\' href=\'https://wordpress.org/news/2017/06/evans/\'>WordPress 4.8 “Evans”</a> <span class=\"rss-date\">June 8, 2017</span><div class=\"rssSummary\">An Update with You in Mind Gear up for a more intuitive WordPress! Version 4.8 of WordPress, named “Evans” in honor of jazz pianist and composer William John “Bill” Evans, is available for download or update in your WordPress dashboard. New features in 4.8 add more ways for you to express yourself and represent your brand. Though some [&hellip;]</div></li></ul></div><div class=\"rss-widget\"><ul><li><a class=\'rsswidget\' href=\'https://wptavern.com/wordcamp-europe-2017-kicks-off-with-contributor-day-focused-on-growing-wordpress-through-inclusion\'>WPTavern: WordCamp Europe 2017 Kicks Off with Contributor Day Focused on Growing WordPress through Inclusion</a></li><li><a class=\'rsswidget\' href=\'https://wptavern.com/lifted-a-wordpress-theme-and-plugin-shop-for-the-marijuana-industry\'>WPTavern: Lifted, a WordPress Theme and Plugin Shop for the Marijuana Industry</a></li><li><a class=\'rsswidget\' href=\'https://wptavern.com/9seeds-acquires-web-savvy-marketings-genesis-theme-store\'>WPTavern: 9seeds Acquires Web Savvy Marketing’s Genesis Theme Store</a></li></ul></div><div class=\"rss-widget\"><ul><li class=\"dashboard-news-plugin\"><span>Popular Plugin:</span> Yoast SEO&nbsp;<a href=\"plugin-install.php?tab=plugin-information&amp;plugin=wordpress-seo&amp;_wpnonce=7eb0784ad9&amp;TB_iframe=true&amp;width=600&amp;height=800\" class=\"thickbox open-plugin-details-modal\" aria-label=\"Install Yoast SEO\">(Install)</a></li></ul></div>", "no");
